import React from "react";
import IconButton from "@material-ui/core/IconButton";
import SearchIcon from "@material-ui/icons/Search";
import Dropzone from "react-dropzone";
import CloudUploadIcon from '@material-ui/icons/CloudUpload'
import Button from "@material-ui/core/Button";
import {makeStyles} from "@material-ui/core";
import {getCurrentBucketZone} from "../../common/bucket";
const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        marginLeft:"32px"
    },
    selected: {
        width: "100 %",
        height: "100 %",
        backgroundColor: "#fff",
        color: "black",
        appearance: "none",
        border: 0,
        display: "block",
        position: "relative",
        mozAppearance:"none",
        webkitAppearance:"none",
        boxShadow:"none",
        outline:"none",
        "&:hover,&:focus": {
            color: "black",
            mozAppearance:"none",
            webkitAppearance:"none",
            outline:"none",
            boxShadow:"none",
            border: 0,
        },
    },
    box:{
        width:16
    },
    button:{
        textTransform:'none',
        zIndex:0,
    },
    actionbutton:{
        textTransform:'none',
    },
    dropzone:{
    }
}));

export default function DashboardHeader(props) {
    const classes = useStyles();
    const search = props.search;
    let inputStr = "";
    const currentBucketPath = getCurrentBucketZone();
    const spaces = props.roots;
    const changeBucketCallBack = props.changeBucketCallBack;
    let selectOptions = []
    if(spaces !=null ){
        spaces.forEach(function (value) {
            selectOptions.push((<option selected={currentBucketPath === value.name} value={value.name}>{value.name}</option>));
        })
    }

    const onKeyDownchange = (e) => {
        if (e.keyCode === 13) {
            search(inputStr);
        }
    }

    const inputChange = (input) => {
        inputStr = input.target.value;
        search(inputStr);
    };

    const handleChange = (event) => {
        changeBucketCallBack(event.target.value);
    };


    return (
        <>
            {/* Navbar */}
            <nav
                className="absolute top-0 left-0 w-full z-10 bg-transparent md:flex-row md:flex-nowrap md:justify-start flex items-center  pt-8">
                <div
                    className="w-full z-10 mx-auto items-start  flex justify-start  md:flex-nowrap flex-wrap md:px-10 px-4">
                    {/* Brand */}
                    <div className="w-full  md:flex hidden flex-row flex-wrap items-center lg:mr-auto mx-auto">
                        <select
                            onChange = {handleChange}
                            className={classes.selected}
                            defaultValue={currentBucketPath}
                        >
                            {selectOptions}
                        </select>
                        <Dropzone
                            // className={classes.dropzone}
                            // onDrop={onDrop}
                            multiple={true}
                        >
                            {({getRootProps, getInputProps}) => (
                                <div className="dropzone" {...getRootProps()}>
                                    <input {...getInputProps()} />
                                    <Button
                                        disableElevation
                                        variant="outlined"
                                        color={"secondary"}
                                        size={'medium'}
                                        // className={classes.button}
                                        startIcon={<CloudUploadIcon/>}>
                                        <span>Upload</span>
                                    </Button>
                                </div>
                            )}
                        </Dropzone>
                        <div  className="bg-gray-100 relative flex w-1/2 flex-wrap items-stretch">
                            <IconButton onClick={function (e){
                                search(inputStr);
                            }} >
                                <SearchIcon />
                            </IconButton>
                            <input
                                onChange={inputChange}
                                onKeyDown={e=> onKeyDownchange(e)}
                                className="w-1/2 border-none  bg-transparent focus:border-red-400 outline-none"
                                placeholder="Search files"
                            />
                        </div>
                    </div>
                </div>
            </nav>
        </>
    );
}
