import './App.css';
import * as React from "react";
import * as user from '././user/user'
import Header from "./components/Header/Header";
import HeaderLinks from "./components/Header/HeaderLinks";
import Button from "@material-ui/core/Button";
import Parallax from "./components/Parallax/Parallax";
import GridContainer from "./components/Grid/GridContainer";
import GridItem from "./components/Grid/GridItem";
const
    bitcoin = require('bitcoinjs-lib')

class App  extends  React.Component{
    constructor(props){
        super(props);
        this.state = {
            userIdentity : null,
            loadingUserIdentity : false
        }
    }

    async componentWillMount() {
        const beginTime = new Date().getTime()/1000;
        const localUserIdentity = await user.getLocalUserIdentity();
        this.setState({
                userIdentity:localUserIdentity
            }
        );
        let loading = document.getElementById('loader');
        if (!loading) {
            return;
        }
        const endTime = new Date().getTime()/1000;
        setTimeout(()=>{
            loading.style.display = 'none'
            this.setState({
                loadingUserIdentity:true
            })
        },2000-(endTime-beginTime));
    }

    render() {
        if (this.state.loadingUserIdentity) {
            if (this.state.userIdentity == null) {
                return (
                    <div>
                        <Header
                            color="transparent"
                            brand="Private . Storage"
                            rightLinks={<HeaderLinks/>}
                            fixed
                            changeColorOnScroll={{
                                height: 400,
                                color: "white",
                            }}
                        />

                        <Parallax filter image={require("assets/img/landing-bg.jpg").default}>
                            <div className={classes.container}>
                                <GridContainer>
                                    <GridItem xs={12} sm={12} md={6}>
                                        <h1 className={classes.title}>Your Story Starts With Us.</h1>
                                        <h4>
                                            Every landing page needs a small description after the big bold
                                            title, that{"'"}s why we added this text here. Add here all the
                                            information that can make you or your product create the first
                                            impression.
                                        </h4>
                                        <br />
                                        <Button
                                            color="danger"
                                            size="lg"
                                            href="https://www.youtube.com/watch?v=dQw4w9WgXcQ&ref=creativetim"
                                            target="_blank"
                                            rel="noopener noreferrer"
                                        >
                                            <i className="fas fa-play" />
                                            Watch video
                                        </Button>
                                    </GridItem>
                                </GridContainer>
                            </div>
                        </Parallax>

                    </div>
                );
            } else {

            }
        } else {
            return (
                <div/>
            );
        }
    }
}


export default App;
