import './App.css';
import * as React from "react";
import PrivateLandingPage from "../PrivateLandingPage";
import * as user from "../../functions/user/user";

class App  extends  React.Component{

    constructor(props){
        super(props);
        this.state = {
            userIdentity : null,
            loadingUserIdentity : false
        }
    }

    async componentWillMount() {
        const beginTime = new Date().getTime()/1000;
        const localUserIdentity = await user.getLocalUserIdentity();
        this.setState({
                userIdentity:localUserIdentity
            }
        );
        let loading = document.getElementById('loader');
        if (!loading) {
            return;
        }
        const endTime = new Date().getTime()/1000;
        setTimeout(()=>{
            loading.style.display = 'none'
            this.setState({
                loadingUserIdentity:true
            })
        },2000-(endTime-beginTime));
    }

    render() {
        if (this.state.loadingUserIdentity) {
            if (this.state.userIdentity == null) {
                return <PrivateLandingPage/>
            } else {
                return (
                    <div/>
                );
            }
        } else {
            return (
                <div/>
            );
        }
    }
}


export default App;
