import React from "react";
import {Link} from "react-router-dom";
import IconButton from "@material-ui/core/IconButton";
import SearchIcon from "@material-ui/icons/Search";
import InputBase from "@material-ui/core/InputBase";
import {Input} from "@material-ui/core";
import makeStyles from "@material-ui/core/styles/makeStyles";

const useStyles = makeStyles(theme => ({
    root: {
        padding: "2px 18px",
        display: "flex",
        alignItems: "center",
        width: 400
    },
    focused: { //<---- see here
        backgroundColor: "none",
        borderColor: "2px solid green",
        borderWidth: 10
    }}))

export default function DashboardHeader(props) {
    const [navbarOpen, setNavbarOpen] = React.useState(false);
    const classes = useStyles();

    const exit = props.exit;
    return (
        <nav
            className={
                (props.transparent
                    ? "top-0 absolute z-50 w-full"
                    : "relative shadow-lg bg-white shadow-lg") +
                " flex flex-wrap items-center justify-between px-2 py-3 "
            }
        >
            <div className="container px-4 mx-auto flex flex-wrap items-center justify-between">
                <div className=" w-full relative flex justify-between lg:w-auto lg:static lg:block lg:justify-start">
                    <a
                        className={
                            (props.transparent ? "text-white" : "text-gray-800") +
                            " text-sm font-bold leading-relaxed inline-block mr-4 py-2 whitespace-nowrap uppercase"
                        }
                    >
                        An IPFS Space
                    </a>

                    <button
                        className="cursor-pointer text-xl leading-none px-3 py-1 border border-solid border-transparent rounded bg-transparent block lg:hidden outline-none focus:outline-none"
                        type="button"
                        onClick={() => setNavbarOpen(!navbarOpen)}
                    >
                        <h2
                            className={
                                (props.transparent ? "text-white" : "text-gray-800") +
                                " fas fa-bars"
                            }
                        >â‰¡</h2>
                    </button>
                </div>

                <div className="w-1/2 bg-gray-100 inline-block items-center mr-4 ">
                    <IconButton >
                        <SearchIcon />
                    </IconButton>
                    <input
                        className="w-10/12 border-none  bg-none"
                        placeholder="Search files"
                    />
                </div>

                <div
                    className={
                        "lg:flex flex-grow items-center bg-white lg:bg-transparent lg:shadow-none" +
                        (navbarOpen ? " block rounded shadow-lg" : " hidden")
                    }
                    id="example-navbar-warning"
                >
                    <ul className="flex flex-col lg:flex-row list-none mr-auto">
                        <li className="flex items-center">
                            <a
                                className={
                                    (props.transparent
                                        ? "lg:text-white lg:hover:text-gray-300 text-gray-800"
                                        : "text-gray-800 hover:text-gray-600") +
                                    " px-3 py-4 lg:py-2 flex items-center text-xs uppercase font-bold"
                                }
                            >
                                <i
                                    className={
                                        (props.transparent
                                            ? "lg:text-gray-300 text-gray-500"
                                            : "text-gray-500") +
                                        " far fa-file-alt text-lg leading-lg mr-2"
                                    }
                                />{" "}
                                {/*Docs*/}
                            </a>
                        </li>
                    </ul>
                    <ul className="flex flex-col lg:flex-row list-none lg:ml-auto">
                        <li className="flex items-center">
                            <button
                                onClick={exit}
                                className={
                                    "text-black  text-xs font-bold uppercase  px-4 py-2 bg-transparent   outline-none focus:outline-none lg:mr-1 lg:mb-0 ml-3 mb-3"
                                }
                                type="button"
                                style={{transition: "all .15s ease"}}
                            >
                                <i className="fas fa-arrow-alt-circle-down"></i> Login Out
                            </button>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    );
}
