import * as React from "react";
import '../../index.css'
import {
    createSeedByMnemonic,
    getLocalUserIdentity,
    saveLocalUserIdentity,
    userIdentityFromSeed
} from "../../common/user";
import {bucketLinks, getBucketKey} from "../../common/bucket";

export class UserIdentityPage extends React.Component{
    constructor(props){
        super(props);
        if(this.props.location.query==null){
            const userIdentity = getLocalUserIdentity();
            if (userIdentity !=null){
                this.state = {
                    userIdentity:userIdentity.toString()
                };
            } else{
                this.props.history.push({ pathname:"/mnemonic/page"});
            }
        }else{
            this.state = {
                userIdentity:this.props.location.query.userIdentity,
                seed:null
            };
        }
    }

     async componentWillMount() {
         let loading = document.getElementById('loader');
         loading.style.display = 'none';

         if (this.state == null || this.state.userIdentity == null) {
             this.props.history.push({pathname: "/mnemonic/page"});
             return;
         }
         const seed = createSeedByMnemonic(this.state.userIdentity);
         const userIdentity = userIdentityFromSeed(seed);
         saveLocalUserIdentity(userIdentity.toString());
         const {bucketKey, buckets} = await getBucketKey(userIdentity);
         const links = await bucketLinks(buckets,bucketKey);
         const query = {
             bucketKey:bucketKey,
             buckets:buckets,
             links:links,
         };
         this.props.history.push({pathname: "/dashBoard",query : query})
     }

    render() {
        return (
            <div>
                <div className="loader" id="loader">
                    <div className="square"/>
                    <div className="square"/>
                    <div className="square"/>
                    <div className="square"/>
                    <div className="square"/>
                </div>
                <div className="loadingtext">
                    <h6 className={"loadingh5"}>
                        创建您的空间中，可能需要等待一会...
                    </h6>
                </div>
            </div>
        );
    }
}
