import * as React from "react";
import Header from "../../components/Header/Header";
import {makeStyles} from "@material-ui/core/styles";
import styles from "./mnemonic";
import GridItem from "../app/PrivateLandingPage";
import GridContainer from "../../components/Grid/GridContainer";

const useStyles = makeStyles(styles);
export default function CreateMnemonicPage(props) {
    const classes = useStyles();
    return (
        <div>
            <Header
                color="transparent"
                brand="Private.Storage"
                fixed
                changeColorOnScroll={{
                    color: "white",
                }}
            />
            <div className={classes.container}>
                <GridContainer>
                    <GridItem xs={12} sm={12} md={6}>
                        <h1 className={classes.title}>把数据存放到一个安全，私密的地方</h1>
                        <h4>
                            Private.Storage是一个不被企业控制的并且更加隐私，安全的存储服务平台。
                            基于IPFS和Filecoin的分布式存储网络可确保您的数据在长久的时间范围内被安全、
                            可靠、高度私密性地存储
                        </h4>
                        <br />

                    </GridItem>
                </GridContainer>
                <div className="create-mnemonic-tips">
                    <div>
                        <h1 className={classes.title}>账户助记词</h1>
                        <h4>
                            您的账户助记词可以帮助您轻松备份和恢复个人账户。
                            警告：切勿向他人透露您的账户助记词。任何人一旦
                            持有该账户助记词，即可控制您的 Ether
                        </h4>
                    </div>
                </div>
            </div>

        </div>
    );
}
