import React, {useCallback} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import SearchIcon from '@material-ui/icons/Search';
import CloudUploadIcon from '@material-ui/icons/CloudUpload'
import Button from "@material-ui/core/Button";
import Dropzone from "react-dropzone";
import {onDropToUpload} from "../../common/upload";
import {CreateNewFolder, ExpandMore} from "@material-ui/icons";
import Box from "@material-ui/core/Box";

const useStyles = makeStyles((theme) => ({
    root: {
        margin:'32px 32px',
        marginBottom:'16px',
        marginTop:'100px',
        padding: '2px 4px',
        paddingRight:'16px',
        display: 'flex',
        alignItems: 'center',
        width:800
    },
    input: {
        marginLeft: theme.spacing(1),
        flex: 1,
        width: 400,
    },
    iconButton: {
        padding: 10,
    },
    divider: {
        height: 28,
        marginLeft: 16,
        marginRight:16,
        marginTop:4,
        marginBottom:4
    },
    box:{
      width:16
    },
    button:{
        textTransform:'none',
    },
    actionbutton:{
        color:'white',
        textTransform:'none',
        backgroundColor:"#93F",
        "&:hover,&:focus": {
            color: "white",
            background: "rgba(153, 51, 255,255)",
        },
    },
    dropzone:{
    }
}));

export default function CustomizedDropZone(props) {
    const classes = useStyles();
    const bucketKey = props.bucketKey;
    const buckets = props.buckets;
    const callback = props.callback;
    const checked = props.checked;
    const newFolder = props.newFolder;
    const selectAll= checked==null?0:checked.length>0;

    const onDrop = useCallback(acceptedFiles => {
        onDropToUpload(acceptedFiles,buckets,bucketKey, async function (data) {
            callback(data);
        });
    }, [])

    return (
        <Paper component="form" className={classes.root}>
            <IconButton className={classes.iconButton} aria-label="menu">
                <SearchIcon />
            </IconButton>
            <InputBase
                className={classes.input}
                placeholder="Search files"
                inputProps={{ 'aria-label': 'search google maps' }}
            />
            {/*<IconButton type="submit" className={classes.iconButton} aria-label="search">*/}
                {/*<SearchIcon />*/}
            {/*</IconButton>*/}
            <Divider className={classes.divider} orientation="vertical" />
            {/*<IconButton color="primary" className={classes.iconButton} aria-label="directions">*/}
                {/*<DirectionsIcon />*/}
            {/*</IconButton>*/}

            <Dropzone
                className={classes.dropzone}
                onDrop={onDrop}
                multiple={true}
            >
                {({getRootProps, getInputProps}) => (
                    <div className="dropzone" {...getRootProps()}>
                        <input {...getInputProps()} />
                        <Button
                            variant="contained"
                            color={"secondary"}
                            size={'medium'}
                            className={classes.button}
                            startIcon={<CloudUploadIcon/>}>
                            <span>Upload</span>
                        </Button>
                    </div>
                )}
            </Dropzone>
            <Box className={classes.box}/>
            <Button
                onClick={newFolder}
                variant="contained"
                color={"primary"}
                size={'medium'}
                className={classes.button}
                startIcon={<CreateNewFolder/>}>
                New Folder
                </Button>
            <Box className={classes.box}/>
            <Button
                className={classes.actionbutton}
                variant="contained"
                disabled={!selectAll}
                size={'medium'}
                endIcon={<ExpandMore/>}>
                Action
            </Button>
        </Paper>
    );
}
