import * as React from "react";
import {useCallback} from "react";
import {useDropzone} from "react-dropzone";
import {onDropToUpload} from "../../common/upload";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import './Dashboard.css'
import {
    getLocalUserIdentity,
} from "../../common/user";
import {getBucketKey} from "../../common/bucket";
import Dropzone from "react-dropzone";

export class DashboardPage extends React.Component {
    constructor(props) {
        super(props);
        if(props.location.query!=null){
            this.state = {
                bucketKey: props.location.query.bucketKey,
                buckets: props.location.query.buckets,
                links: props.location.query.links,
            }
        }
    }

    async componentWillMount() {
        if (this.state==null || this.state.bucketKey == null || this.state.buckets == null) {
            const userIdentity = await  getLocalUserIdentity();
            if (userIdentity != null) {
                const {bucketKey, buckets} = await getBucketKey(userIdentity);
                this.setState({
                    bucketKey:bucketKey,
                    buckets:buckets,
                })
            } else {
                this.props.history.push({pathname: "/mnemonic"});
            }
        }
        let loading = document.getElementById('loader');
        loading.style.display = 'none';
    }

    render() {
        if(this.state==null || this.state.buckets == null || this.state.bucketKey ==null){
            return <div/>
        }
        return (
            <Paper className='paper'>
            <MyDropZone bucketKey={this.state.bucketKey} buckets={this.state.buckets}/>
            </Paper>
        );
    }
}

function MyDropZone(props) {
     const bucketKey = props.bucketKey;
     const buckets = props.buckets;

    const onDrop = useCallback(acceptedFiles => {
        onDropToUpload(acceptedFiles,buckets,bucketKey);
    }, [])

    return (
        <Dropzone
            onDrop={onDrop}
            multiple={true}
        >
            {({getRootProps, getInputProps}) => (
                <div className="dropzone" {...getRootProps()}>
                    <input {...getInputProps()} />
                    <Button variant="contained" color={"secondary"}>
                        <span>Upload</span>
                    </Button>
                </div>
            )}
        </Dropzone>
    )
}
