import * as React from "react";
import '../../index.css'
import {createSeedByMnemonic, userIdentityFromSeed} from "../../functions/user/user";

export class UserIdentityPage extends React.Component{
    constructor(props){
        super(props);
        if(this.props.location.query.userIdentity==null){
            const history = useHistory();
            history.push("/mnemonic/page");
        }else{
            this.state = {
                userIdentity:this.props.location.query.userIdentity,
                seed:null
            };
        }
    }

     componentWillMount() {
        const seed =  createSeedByMnemonic(this.state.userIdentity);
        const userIdentity = userIdentityFromSeed(seed);
        this.setState(
            {
                seed: userIdentity
            }
        )
    }

    render() {
        return (
            <div>
                <div className="loader" id="loader">
                    <div className="square"/>
                    <div className="square"/>
                    <div className="square"/>
                    <div className="square"/>
                    <div className="square"/>
                </div>
                <div className="loadingtext">
                    <h6 className={"loadingh5"}>
                        {/*创建您的秘钥中...*/}
                        {this.state.seed}
                    </h6>
                </div>
            </div>
        );
    }
}
