import * as React from "react";
import '../../index.css'
import {createSeedByMnemonic, userIdentityFromSeed} from "../../common/user";

export class UserIdentityPage extends React.Component{
    constructor(props){
        super(props);
        if(this.props.location.query==null){
            this.props.history.push({ pathname:"/mnemonic/page"});
        }else{
            this.state = {
                userIdentity:this.props.location.query.userIdentity,
                seed:null
            };
        }
    }

     componentWillMount() {
        if(this.state==null || this.state.userIdentity==null){
             this.props.history.push({ pathname:"/mnemonic/page"});
             return;
        }
         const seed =  createSeedByMnemonic(this.state.userIdentity);
         const userIdentity = userIdentityFromSeed(seed);
         localStorage.setItem("identity",userIdentity);
         this.setState(
             {
                 seed: userIdentity
             }
         )
    }

    render() {
        return (
            <div>
                <div className="loader" id="loader">
                    <div className="square"/>
                    <div className="square"/>
                    <div className="square"/>
                    <div className="square"/>
                    <div className="square"/>
                </div>
                <div className="loadingtext">
                    <h6 className={"loadingh5"}>
                        创建您的秘钥中...
                    </h6>
                </div>
            </div>
        );
    }
}
