import * as React from "react";
import {useCallback} from "react";
import {useDropzone} from "react-dropzone";
import {onDropToUpload} from "../../common/upload";
import Button from "@material-ui/core/Button";

export class DashboardPage extends React.Component {
    constructor(props) {
        super(props);

        if(props.location.query==null){
            props.history.push({ pathname:"/mnemonic/page"});
        }

        this.state = {
            bucketKey: props.location.query.bucketKey,
            buckets: props.location.query.buckets,
            links: props.location.query.links,
        }
    }

    componentWillMount() {
        let loading = document.getElementById('loader');
        loading.style.display = 'none';
    }

    render() {
        return (
            <MyDropZone bucketKey={this.state.bucketKey} buckets={this.state.buckets}/>
        );
    }
}

function MyDropZone(props) {
     const bucketKey = props.bucketKey;
     const buckets = props.buckets;

    const onDrop = useCallback(acceptedFiles => {
        onDropToUpload(acceptedFiles,buckets,bucketKey);
    }, [])
    const {getRootProps, getInputProps} = useDropzone({onDrop})

    return (
        <div {...getRootProps()}>
            <input {...getInputProps()} />
            <div>
                <Button variant="contained" color={"secondary"}>
                    上传
                </Button>
            </div>
        </div>
    )
}
