import * as bip39 from "bip39";
import {pbkdf2Sync} from "crypto";
import {PrivateKey} from '@textile/hub'

/**
 * 创建助词器
 */
export    function createMnemonic() {
    var bip39Mnemonic = bip39.generateMnemonic();
    console.log("mnemonic:"+bip39Mnemonic)
    return bip39Mnemonic
}

export function createSeedByMnemonic(mnemonic) {
    const seed= pbkdf2Sync(mnemonic, 'salt', 1024, 32, 'sha512');
    return seed
}

export async  function getLocalUserIdentity(){
    var localUserIdentity = localStorage.getItem("private.user.identity");
    if( localUserIdentity==null){
        return null;
    }
    return localUserIdentity
}

export  function userIdentityFromSeed(seed){
    const identity = new PrivateKey(seed,'ed25519');
    return identity.toString();
}

async function getIdentity()  {
    try {
        var storedIdent = localStorage.getItem("pirvate.identity");
        if (storedIdent === null) {
            throw new Error('No identity')
        }
        const restored = PrivateKey.fromString(storedIdent);
        return restored
    }
    catch (e) {
        /**
         * If any error, create a new identity.
         */
        try {
            // const identity = PrivateKey.fromRandom()
            const identity = new PrivateKey(createSeedByMnemonic(createMnemonic()),'ed25519')
            const identityString = identity.toString();
            console.log(identityString);
            localStorage.setItem("identity",identityString);
            return identity
        } catch (err) {
            return err.message
        }
    }
}
