import * as React from "react";
import {makeStyles} from "@material-ui/core/styles";
import styles from "../../assets/jss/material-kit-react/views/landingPage.js";
import Parallax from "../../components/Parallax/Parallax";
import GridContainer from "../../components/Grid/GridContainer";
import GridItem from "../../components/Grid/GridItem";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import {createMnemonic} from "../../common/user";
import ActionLinks from "./ActionLinks";
import SignUpHeader from "../../components/comps/SignUpHeader";
const useStyles = makeStyles(styles);
export default  function CreateMnemonicPage(props) {
    let loading = document.getElementById('loader');
    loading.style.display = 'none';
    const mnemonic =  createMnemonic();
    const classes = useStyles();
    return (
        <div>
            <div>
                <SignUpHeader/>

                <div className="mt-12 container relative ">
                    <div className=" flex flex-col ">
                        <div className="w-full lg:w-6/12 px-4 ml-auto mr-auto ">
                            <div >
                                <h1 className="text-white font-semibold text-5xl ">
                                    Account mnemonic
                                </h1>
                                <p className="mt-6 text-lg text-gray-300">
                                    Your account mnemonic phrase is your key to log in to the storage space.
                                    <br/>Warning: Do not disclose your account mnemonic phrase to others. Once anyone has the mnemonic phrase for the account, he can view the data you uploaded. Please record the mnemonic phrase on multiple sheets of paper and store them in 2-3 different places.
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
                <Parallax>
                    <div className={classes.container}>
                        <GridContainer>
                            <GridItem xs={12} sm={12} md={6}>
                                <h1 className={classes.title}>账户助记词</h1>
                                <h4>
                                    您的账户助记词可以帮助您轻松备份和恢复您的存储账户。<br/>
                                    警告：切勿向他人透露您的账户助记词。任何人一旦
                                    持有该账户助记词，即可查看您上传的数据
                                </h4>
                                <h5>
                                    请将下方的账户助记词记录在纸上，并保存到安全的地方,
                                    如果希望提升信息安全性，请将信息记录在多张纸上，并分别保存在2 - 3个不同的
                                    地方
                                </h5>

                                <Card>
                                    <CardContent>
                                        {mnemonic}
                                    </CardContent>
                                </Card>
                                <ActionLinks user={{mnemonic}}/>
                            </GridItem>
                        </GridContainer>
                    </div>
                </Parallax>
            </div>
        </div>
    );
}
