import React from "react";
import {Link} from "react-router-dom";
import IconButton from "@material-ui/core/IconButton";
import SearchIcon from "@material-ui/icons/Search";
import InputBase from "@material-ui/core/InputBase";
import {Input} from "@material-ui/core";
import makeStyles from "@material-ui/core/styles/makeStyles";

const useStyles = makeStyles(theme => ({
    root: {
        padding: "2px 18px",
        display: "flex",
        alignItems: "center",
        width: 400
    },
    focused: { //<---- see here
        backgroundColor: "none",
        borderColor: "2px solid green",
        borderWidth: 10
    }}))

export default function DashboardHeader(props) {
    const search = props.search;
    let inputStr = "";
    const onKeyDownchange = (e) => {
        if (e.keyCode === 13) {
            search(inputStr);
        }
    }

    const inputChange = (input) => {
        inputStr = input.target.value;
        search(inputStr);
    };

    const exit = props.exit;
    return (
        <>
            {/* Navbar */}
            <nav

                className="absolute top-0 left-0 w-full z-10 bg-transparent md:flex-row md:flex-nowrap md:justify-start flex items-center p-4">
                <div
                    className="w-full mx-autp items-center flex justify-between md:flex-nowrap flex-wrap md:px-10 px-4">
                    {/* Brand */}
                    <div
                        className="border-0 mt-3 mx-3 my-3  placeholder-blueGray-300 text-blueGray-600 relative bg-white bg-white rounded text-sm shadow outline-none focus:outline-none focus:ring w-full pl-10"

                    >
                        <IconButton >
                            <SearchIcon />
                        </IconButton>
                        <InputBase

                            onChange={inputChange}
                            onKeyDown={e => onKeyDownchange(e)}
                            className="w-10/12 border-transparent focus:border-transparent focus:ring-0"
                            placeholder="Search files"
                        />
                    </div>

                    {/* Form */}
                    <form className="md:flex hidden flex-row flex-wrap items-center lg:ml-auto mr-3">
                        <div className="relative flex w-full flex-wrap items-stretch">
                            <span
                                className="z-10 h-full leading-snug font-normal absolute text-center text-blueGray-300 absolute bg-transparent rounded text-base items-center justify-center w-8 pl-3 py-3">
                             <i className="fas fa-search"></i>
                            </span>
                            <input
                                className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 relative bg-white bg-white rounded text-sm shadow outline-none focus:outline-none focus:ring w-full pl-10"
                                onChange={inputChange}
                                onKeyDown={e => onKeyDownchange(e)}
                                placeholder="Search files"
                            />
                        </div>
                    </form>
                </div>
            </nav>
        </>
    );
}
